import { Component } from '@angular/core';
import { TaskComponent } from '../task/task.component';
import { Router } from '@angular/router';
import { MyServiceService } from '../my-service.service';

@Component({
  selector: 'app-inbox',
  templateUrl: './inbox.component.html',
  styleUrls: ['./inbox.component.css']
})
export class InboxComponent {
  tasks: TaskComponent[] | undefined;
  constructor(private router:Router,private serv:MyServiceService){}
  ngOnInit(){
    this.getTasks();
  }
  private getTasks(){
    this.serv.getAllTasks().subscribe(data => {
      this.tasks = data;
    });
  }
  updateTask(id:number){
    this.router.navigate(['/user/update-doctor',id]);
  }
  deleteTask(id:number){
    this.serv.deleteTask(id).subscribe( data => {
      console.log(data);
      this.getTasks();
    })
  }
}
