import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UserInfoComponent } from '../user-info/user-info.component';
import { MyServiceService } from '../my-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  loginForm = new FormGroup({
    id:new FormControl('',[Validators.required]),
    emailId:new FormControl('',[Validators.required,Validators.pattern('^[a-z0-9]+@[gmail]+\.com$')]),
    password:new FormControl('',[Validators.required,Validators.pattern('(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%&*_^]).{8,}')])
  })
  user:UserInfoComponent=new UserInfoComponent();
  user1:UserInfoComponent=new UserInfoComponent();
  constructor(private route:Router,private serv:MyServiceService){

  }
  get Id(){
    return this.loginForm.get('id');
  }
  get EmailId(){
    return this.loginForm.get('emailId');
  }

  get Password(){
    return this.loginForm.get('password');
  }
  user_id:number=this.user.id;

  // ngOnInit(): void {
  //   this.serv.getUserById(this.user.id).subscribe(data => {
  //     this.user1 = data;
  //   }, error => console.log(error));
  // }
  getUser(){
    this.serv.getUserById(this.user.id).subscribe(data => {
      console.log(data);
      this.user=data;
    }, error => console.log(error));
  }
  loginFormSubmit(){
    this.getUser();
    console.log(typeof(this.user.id));
    console.log(typeof(this.user.email_address));
  //  console.log(typeof(this.user1.email_address));
  //  console.log(this.user1.id);
  //  console.log(this.user1.email_address);
  console.log(this.user.id);
   console.log(this.user.email_address);
   console.log(this.user.password); 
   if(this.user.id== this.user1.id && this.user.email_address == this.user1.email_address && this.user.password==this.user1.password){
      this.route.navigate(['/user/task-inbox']);
   }
   else{
    alert("Invalid Credentials");
   }

  }
}
