import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UserInfoComponent } from '../user-info/user-info.component';
import { MyServiceService } from '../my-service.service';

@Component({
  selector: 'app-create-account',
  templateUrl: './create-account.component.html',
  styleUrls: ['./create-account.component.css']
})
export class CreateAccountComponent {
  user: UserInfoComponent = new UserInfoComponent();
  registerForm = new FormGroup({
    id:new FormControl('',[Validators.required]),
    name:new FormControl('',[Validators.required,Validators.pattern('^[A-Za-z ]*$')]),
    emailId:new FormControl('',[Validators.required,Validators.pattern('^[a-z0-9]+@[gmail]+\.com$')]),
    password:new FormControl('',[Validators.required,Validators.pattern('(?=.*[A-Z])(?=.*[a-z])(?=.*[0-9])(?=.*[!@#$%&*_^]).{8,}')]),
    contact_no:new FormControl('',[Validators.required])
  })
  constructor(private serv: MyServiceService,private route:Router){}
  get Id(){
    return this.registerForm.get('id');
  }
  get Name(){
    return this.registerForm.get('name');
  }
  get EmailId(){
    return this.registerForm.get('emailId');
  }
  get Contact_No(){
    return this.registerForm.get('contact_no');
  }
  get Password(){
    return this.registerForm.get('password');
  }

  registerFormSubmit(){
    this.saveUser();
    alert("Added User Successfully");
    this.route.navigate(['/login']); 
  }
  saveUser() {
    this.serv.createUser(this.user).subscribe( data =>{
      console.log(data);
    },
    error => console.log(error));
  }
}
